select  u.id_utilisateur,nom,prenom, extract('year' from date_observation) as Annee,count(distinct id_citation) as nb_citations from utilisateur u,observations_observateurs oo, observations o,citations c where oo.id_utilisateur not in (select distinct oo.id_utilisateur from observations o,observations_observateurs oo where o.id_observation=oo.id_observation and oo.id_observation <= 327522 and extract('year' from date_observation) < 2012) and oo.id_utilisateur=u.id_utilisateur and oo.id_observation=o.id_observation and c.id_observation=o.id_observation group by u.id_utilisateur,nom,prenom, extract('year' from date_observation) order by extract('year' from date_observation) desc, count(distinct id_citation) desc;